// Custom Service Worker code for AeroLens notifications
// This is imported by the main service worker generated by vite-plugin-pwa

self.addEventListener('notificationclick', (event) => {
  event.notification.close()

  const data = event.notification.data || {}
  const actionUrl = data.actionUrl || '/'

  // Open the app or focus existing window
  event.waitUntil(
    clients.matchAll({ type: 'window', includeUncontrolled: true }).then((clientList) => {
      // Check if app is already open
      for (const client of clientList) {
        if (client.url.includes(self.location.origin) && 'focus' in client) {
          // Navigate to the action URL and focus
          client.navigate(actionUrl)
          return client.focus()
        }
      }
      // Open new window if not already open
      if (clients.openWindow) {
        return clients.openWindow(actionUrl)
      }
    })
  )
})

// Handle notification close
self.addEventListener('notificationclose', (event) => {
  const data = event.notification.data || {}
  // Could log analytics here
  console.log('Notification closed:', data.alertType)
})

// Handle push events (for future server-sent push notifications)
self.addEventListener('push', (event) => {
  if (!event.data) return

  try {
    const payload = event.data.json()

    const options = {
      body: payload.body || '',
      icon: payload.icon || '/logo.png',
      badge: '/favicon.svg',
      tag: payload.tag || 'aerolens-notification',
      data: payload.data || {},
      vibrate: [200, 100, 200],
      requireInteraction: payload.requireInteraction || false,
    }

    event.waitUntil(self.registration.showNotification(payload.title || 'AeroLens', options))
  } catch (error) {
    console.error('Error handling push event:', error)
  }
})
